import configparser
import os
import base64


def ico_check():
    if os.path.exists(os.getenv('APPDATA') + "\\TomatoIni\\tomato.ico"):
        pass
    else:
        ico_generate()


def ico_generate():
    txt2img = b''
    imgdata = base64.b64decode(txt2img)
    with open(os.getenv('APPDATA') + "\\TomatoIni\\tomato.ico", 'wb') as f:
        f.write(imgdata)


def ini_check():
    if os.path.exists(os.getenv('APPDATA') + "\\TomatoIni"):
        pass
    else:
        os.mkdir(os.getenv('APPDATA') + "\\TomatoIni")

    ini_create()
    ico_check()


def ini_create():
    if os.path.exists(os.getenv('APPDATA') + "\\TomatoIni\\config.ini"):
        pass
    else:
        with open(os.getenv('APPDATA') + "\\TomatoIni\\config.ini", 'w') as f:
            inifile = configparser.ConfigParser()
            inifile.read(os.getenv('APPDATA') + "\\TomatoIni\\config.ini")
            inifile.add_section("ptime")
            inifile.set("ptime", "ini_total_time", "0")
            inifile.set("ptime", "ini_default_time", "2700")
            inifile.write(f)


def time2txt(t_time):
    t1 = t_time % 60  # 总秒数
    t11 = t1 % 10  # 秒 个位
    t12 = t1 // 10  # 秒 十位
    t2 = t_time // 60  # 总分钟
    t21 = t2 % 10  # 分 个位
    t22 = t2 // 10  # 分 十位
    str1 = "{}{}:{}{}".format(t22, t21, t12, t11, sep="")
    return str1


def time2txt2(t_time):
    t1 = t_time // 60  # 总分钟
    t2 = t1 % 60  # 分钟数
    t3 = t1 // 60  # 总小时
    str1 = "{} h {} m".format(t3, t2)
    return str1


class PCount:
    def __init__(self):
        # 将ini文件中的total_time和default_time读入
        inifile = configparser.ConfigParser()
        inifile.read(os.getenv('APPDATA') + "\\TomatoIni\\config.ini")
        self.total_time = int(inifile.get("ptime", "ini_total_time"))
        self.default_time = int(inifile.get("ptime", "ini_default_time"))
        self.tmp_time = self.default_time

    # 将total_time更新到ini文件中
    def update_ini(self):
        inifile = configparser.ConfigParser()
        inifile.read(os.getenv('APPDATA') + "\\TomatoIni\\config.ini")
        inifile.set("ptime", "ini_total_time", str(self.total_time))
        with open(os.getenv('APPDATA') + "\\TomatoIni\\config.ini", 'w') as f:
            inifile.write(f)
